import { makeStyles } from "@material-ui/styles";
import styled from "styled-components";

export const Container = styled.div`
  display: flex;
  margin-left:150px;
  flex-direction: row;
  flex-wrap: wrap;
  width: 1920px,
  height:1080px;
  background-color:red
  @media(max-width:1366px){
    width:1366px;
  }
`;

export const Paper = styled.div`
  width: 400px;
  display: flex;
  flex-direction: column;
  align-itens: center;
  height: 500px;
  margin: 10px;
  box-shadow: 0 2px 13px 0 rgba(0, 0, 0, 0.33);
  padding-top: 10px;
  padding-left: 5px;
  @media (max-width: 1366px) {
    width: 300px;
    height: 400px;
  } ;
`;

export const Image = styled.div`
  margin-bottom: 50px;
  width: 200px;
  height: 203px;
  @media (max-width: 1366px) {
    margin-bottom: 40px;
  }
`;

const classe = makeStyles({
  image: {
    marginBottom: 50,
  },

  title: {
    marginTop: "%",
    fontFamily: "Arial",
    fontSize: 20,
    fontWeight: 200,
    marginLeft: 20,
    color: "grey",
    height: 133,
  },
  link: {
    textDecoration: "none",
  },
  preco: {
    color: "#1976d2",
    fontWeight: 1000,
    fontFamily: "Arial",
    fontSize: 30,
    marginBottom: 15,
  },
  precoPai: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
  },
  input: {
    width: "33%",
    height: 33,
    marginTop: "10%",
    marginLeft: "30%",
    borderRadius: 4,
    color: "BLACK",
    background: "white",
  },

  h1: {
    color: "white",
    fontFamily: "Arial",
    fontSize: 25,
    marginLeft: "3%",
    marginTop: "30%",
  },
  botao2: {
    border: "solid 4px #ff4040",
    borderRadius: 5,
    background: "#ff4040",
    marginLeft: 20,
    height: 50,
    position: "relative",
    marginTop: "30%",
  },
  log: {
    width: 50,
    height: 50,
    position: "relative",
    marginTop: "3%",
  },

  login: {
    display: "flex",
    flexDirection: "row",
    justifyContent: "space-between",
    textDecoration: "none",
  },
});

export default classe;
